import React from 'react';
import './App.css';



export default class App extends React.Component {
    constructor (props) {
        super(props)

        this.state = {
            inputValue: '',
            toDoTasks: [ { id: 'asd123', value: 'Get milk' }, { id: 'asd111', value: 'Go Shopping' } ],
            completedTasks: []
        }

        this.handleInputChange = this.handleInputChange.bind(this)
        this.handleAddClick = this.handleAddClick.bind(this)
    }

    handleAddClick () {
        console.log(this.state.inputValue)
    }

    handleInputChange (event) {
        this.setState({ inputValue: event.currentTarget.value })
    }

    getToDoItems () {
        let items = []

        this.state.toDoTasks.forEach((item, index) => {
            let key = `to-do-item-${index}`
            let html = (
                <div className="task" key={key}>
                    <div className="task-value">
                        <input type="checkbox" className="checkbox" />
                        {item.value}
                    </div>
                    <div className="task-delete">
                        <button className="delete-button">Delete</button>
                    </div>
                </div>
            )

            items.push(html)
        })

        return items





        // return (
        //     <div className="task">
        //         <div className="task-value">
        //             <input type="checkbox" className="checkbox" />

        //         </div>
        //         <div className="task-delete">
        //             <button className="delete-button">Delete</button>
        //         </div>
        //     </div>
        // )
    }


    render () {
        return (
            <section className="todo">
                <div>
                    <h2 className="section-name">ADD ITEM</h2>
                    <div className="input">
                        <input type="text" className="input-task" onChange={this.handleInputChange} />
                        <button className="add-task" onClick={this.handleAddClick}>Add</button>
                    </div>
                </div>
                <div>
                    <h2 className="section-name">TODO</h2>
                    <div className="open-tasks">
                        {this.getToDoItems()}
                    </div>
                </div>
                <div>
                    <h2 className="section-name">COMPLETED</h2>
                    <div className="completed-tasks" />
                </div>
            </section>
        )
    }
};
