import React from 'react';
import './App.css';

export default class App extends React.Component {
    static getDerivedStateFromProps(props, state) {
        console.log('something updated')
    }

    constructor (props) {
        super(props)

        let data = window.localStorage.toDoData

        if (data) {
            data = JSON.parse(data)
        } else {
            data = { toDoTasks: [], completedTasks: [] }
            window.localStorage.setItem('toDoData', JSON.stringify(data))
        }

        this.state = {
            inputValue: '',
            toDoTasks: data.toDoTasks,
            completedTasks: data.completedTasks



            // toDoTasks: [ { id: 'asd123', value: 'Get milk' }, { id: 'asd111', value: 'Go Shopping' } ],
            // completedTasks: [ { id: 'vegetarian', value: 'Pancakes' } ]
        }

        this.handleInputChange = this.handleInputChange.bind(this)
        this.handleAddClick = this.handleAddClick.bind(this)
        this.handleDeleteClick = this.handleDeleteClick.bind(this)
        this.handleCheckboxClick = this.handleCheckboxClick.bind(this)
        this.keyPressed = this.keyPressed.bind(this)
    }

    keyPressed(event) {
        if (event.key === "Enter") {
          this.handleAddClick()
        }
    }

    handleAddClick () {
        if (this.state.inputValue) {
            let id = Date.now()
            let newItem = { id: id.toString(), value: this.state.inputValue }
            let newToDoTasks = this.state.toDoTasks
            newToDoTasks.unshift(newItem)
            this.setState({
                inputValue: '',
                toDoTasks: newToDoTasks
            })
        }
    }

    handleDeleteClick (event) {
        let id = event.currentTarget.closest('.task').id
        let checked = event.currentTarget.closest('.task').querySelector('.checkbox').checked;

        if (checked) {
            let newToDoTasks = this.state.completedTasks
            let index = newToDoTasks.findIndex(item => item.id === id);
            newToDoTasks.splice(index, 1);

            this.setState({
                completedTasks: newToDoTasks
            })
        } else {
            let newToDoTasks = this.state.toDoTasks
            let index = newToDoTasks.findIndex(item => item.id === id);
            newToDoTasks.splice(index, 1);

            this.setState({
                toDoTasks: newToDoTasks
            })
        }
    }

    handleCheckboxClick (event) {
        let id = event.currentTarget.closest('.task').id
        let checked = event.currentTarget.closest('.task').querySelector('.checkbox').checked;

        if (checked) {
            let newToDoTasks = this.state.toDoTasks
            let index = newToDoTasks.findIndex(item => item.id === id);
            let newCompletedTasks = this.state.completedTasks
            newCompletedTasks.unshift(newToDoTasks[index]) 
            newToDoTasks.splice(index, 1);
            this.setState({
                toDoTasks: newToDoTasks,
                completedTasks: newCompletedTasks
            })
        } else {
            let newCompletedTasks = this.state.completedTasks
            let index = newCompletedTasks.findIndex(item => item.id === id);
            let newToDoTasks = this.state.toDoTasks
            newToDoTasks.unshift(newCompletedTasks[index])
            newCompletedTasks.splice(index, 1);
            this.setState({
                toDoTasks: newToDoTasks,
                completedTasks: newCompletedTasks
            })
        }
    }

    handleInputChange (event) {
        this.setState({ inputValue: event.currentTarget.value })
    }

    getToDoItems () {
        let items = []
        this.state.toDoTasks.forEach((item) => {
            let key = `to-do-item-${item.id}`
            let html = (
                <div className="task" id={item.id} key={key}>
                    <div className="task-value">
                        <input type="checkbox" className="checkbox" onChange={this.handleCheckboxClick}/>
                        {item.value}
                    </div>
                    <div className="task-delete">
                        <button className="delete-button" onClick={this.handleDeleteClick}>Delete</button>
                    </div>
                </div>
            )
            items.push(html)
        })
        return items
    }

    getCompletedItems () {
        let items = []
        this.state.completedTasks.forEach((item) => {
            let key = `completed-item-${item.id}`
            let html = (
                <div className="task" id={item.id} key={key}>
                    <div className="task-value">
                        <input type="checkbox" className="checkbox" checked={true} onChange={this.handleCheckboxClick}/>
                        {item.value}
                    </div>
                    <div className="task-delete">
                        <button className="delete-button" onClick={this.handleDeleteClick}>Delete</button>
                    </div>
                </div>
            )
            items.push(html)
        })
        return items
    }



    render () {
        return (
            <section className="todo">
                <div>
                    <h2 className="section-name">ADD ITEM</h2>
                    <div className="input">
                        <input value={this.state.inputValue} type="text" className="input-task" onChange={this.handleInputChange} onKeyPress={this.keyPressed} />
                        <button className="add-task" onClick={this.handleAddClick}>Add</button>
                    </div>
                </div>
                <div>
                    <h2 className="section-name">TODO</h2>
                    <div className="open-tasks">
                        {this.getToDoItems()}
                    </div>
                </div>
                <div>
                    <h2 className="section-name">COMPLETED</h2>
                    <div className="completed-tasks">
                        {this.getCompletedItems()}
                    </div>
                </div>
            </section>
        )
    }
};
